
public class Assign {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10, b = 0;		
		b = a;		// b 데이터 안에 a의 데이터를 재할당
		System.out.println("a = " + a);		// a = 10
		System.out.println("b = " + b);		// b = 10
		System.out.println(a + " + " + b + " = " + (a + b));	// 10 + 10 = 20
		
		
	}

}
