
public class Example04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = +1;
		int b = -2;
		int c = 5;
		boolean d = true;
		
		System.out.println(a);	//1
		System.out.println(b);	//-2
		
		System.out.println(!d);	//false
		
		int i = 1;
		System.out.println(i);	//1
		
		i = -i;
		System.out.println(i);	//-1
		
		i = -i;
		System.out.println(i);	//1
		
		i = -i;
		System.out.println(i);	//-1
	}

}
